// src/components/Dashboard.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const API_URL = 'http://localhost:5000/api/customers';

const Dashboard = () => {
  const [customers, setCustomers] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    address: '',
    customerNumber: '',
    meterSerialNumber: '',
  });

  // State to store the ID of the customer being updated
  const [updateCustomerId, setUpdateCustomerId] = useState(null);

  useEffect(() => {
    fetchCustomers();
  }, []);

  const fetchCustomers = async () => {
    try {
      const response = await axios.get(API_URL);
      setCustomers(response.data);
    } catch (error) {
      console.error('Error fetching customers:', error.message);
    }
  };

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(API_URL, formData);
      setFormData({ name: '', address: '', customerNumber: '', meterSerialNumber: '' });
      fetchCustomers();
    } catch (error) {
      console.error('Error creating customer:', error.message);
    }
  };

  const handleUpdateClick = (id) => {
    // Set the ID of the customer to be updated
    setUpdateCustomerId(id);

    // Find the customer with the given ID
    const customerToUpdate = customers.find((customer) => customer._id === id);

    // Populate the form data with the customer details
    setFormData({
      name: customerToUpdate.name,
      address: customerToUpdate.address,
      customerNumber: customerToUpdate.customerNumber,
      meterSerialNumber: customerToUpdate.meterSerialNumber,
    });
  };

  const handleUpdateSubmit = async () => {
    try {
      // Update the customer with the provided ID and the changed fields
      await axios.put(`${API_URL}/${updateCustomerId}`, formData);
      setUpdateCustomerId(null);
      fetchCustomers();
    } catch (error) {
      console.error('Error updating customer:', error.message);
    }
  };

  const handleCancelUpdate = () => {
    // Reset the form data and updateCustomerId
    setFormData({ name: '', address: '', customerNumber: '', meterSerialNumber: '' });
    setUpdateCustomerId(null);
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`${API_URL}/${id}`);
      fetchCustomers();
    } catch (error) {
      console.error('Error deleting customer:', error.message);
    }
  };

  return (
    <div className="container-xl">
      <div className="table-responsive">
        <div className="table-wrapper">
          <div className="table-title">
            <div className="row">
              <div className="col-sm-6">
                <h2>Manage <b>Customers</b></h2>
              </div>
              <div className="col-sm-6">
                <button className="btn btn-success" data-toggle="modal" data-target="#addCustomerModal">
                  <i className="material-icons">&#xE147;</i> <span>Add New Customer</span>
                </button>
              </div>
            </div>
          </div>
          <table className="table table-striped table-hover">
            <thead>
              <tr>
                <th>
                  <span className="custom-checkbox">
                    <input type="checkbox" id="selectAll" />
                    <label htmlFor="selectAll"></label>
                  </span>
                </th>
                <th>Name</th>
                <th>Address</th>
                <th>Customer Number</th>
                <th>Meter Serial Number</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {customers.map((customer) => (
                <tr key={customer._id}>
                  <td>
                    <span className="custom-checkbox">
                      <input type="checkbox" id={`checkbox${customer._id}`} name="options[]" value="1" />
                      <label htmlFor={`checkbox${customer._id}`}></label>
                    </span>
                  </td>
                  <td>{customer.name}</td>
                  <td>{customer.address}</td>
                  <td>{customer.customerNumber}</td>
                  <td>{customer.meterSerialNumber}</td>
                  <td>
                    <button className="btn btn-warning" onClick={() => handleUpdateClick(customer._id)}>
                      Update
                    </button>
                    <button className="btn btn-danger" onClick={() => handleDelete(customer._id)}>
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
      {/* Add Customer Modal */}
      <div id="addCustomerModal" className="modal fade">
        <div className="modal-dialog">
          <div className="modal-content">
            <form onSubmit={handleSubmit}>
              <div className="modal-header">
                <h4 className="modal-title">Add Customer</h4>
                <button type="button" className="close" data-dismiss="modal" aria-hidden="true">&times;</button>
              </div>
              <div className="modal-body">
                {/* Your form fields for adding a new customer */}
              </div>
              <div className="modal-footer">
                <button type="button" className="btn btn-default" data-dismiss="modal">Cancel</button>
                <button type="submit" className="btn btn-success">Add</button>
              </div>
            </form>
          </div>
        </div>
      </div>
      {/* Edit Customer Modal */}
      {updateCustomerId && (
        <div id="editCustomerModal" className="modal fade">
          <div className="modal-dialog">
            <div className="modal-content">
              <form onSubmit={handleUpdateSubmit}>
                <div className="modal-header">
                  <h4 className="modal-title">Edit Customer</h4>
                  <button type="button" className="close" data-dismiss="modal" aria-hidden="true">&times;</button>
                </div>
                <div className="modal-body">
                  {/* Your form fields for editing an existing customer */}
                </div>
                <div className="modal-footer">
                  <button type="button" className="btn btn-default" onClick={handleCancelUpdate}>Cancel</button>
                  <button type="submit" className="btn btn-info">Update</button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
