// src/components/Dashboard.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const API_URL = 'http://localhost:5000/api/customers';

const Dashboard = () => {
  const [customers, setCustomers] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    address: '',
    customerNumber: '',
    meterSerialNumber: '',
  });

  // State to store the ID of the customer being updated
  const [updateCustomerId, setUpdateCustomerId] = useState(null);

  useEffect(() => {
    fetchCustomers();
  }, []);

  const fetchCustomers = async () => {
    try {
      const response = await axios.get(API_URL);
      setCustomers(response.data);
    } catch (error) {
      console.error('Error fetching customers:', error.message);
    }
  };

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(API_URL, formData);
      setFormData({ name: '', address: '', customerNumber: '', meterSerialNumber: '' });
      fetchCustomers();
    } catch (error) {
      console.error('Error creating customer:', error.message);
    }
  };

  const handleUpdateClick = (id) => {
    // Set the ID of the customer to be updated
    setUpdateCustomerId(id);
  };

  const handleUpdateSubmit = async () => {
    try {
      // Update the customer with the provided ID and the changed fields
      await axios.put(`${API_URL}/${updateCustomerId}`, formData);
      setUpdateCustomerId(null);
      fetchCustomers();
    } catch (error) {
      console.error('Error updating customer:', error.message);
    }
  };

  const handleCancelUpdate = () => {
    // Reset the form data
    setFormData({ name: '', address: '', customerNumber: '', meterSerialNumber: '' });
    setUpdateCustomerId(null);
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`${API_URL}/${id}`);
      fetchCustomers();
    } catch (error) {
      console.error('Error deleting customer:', error.message);
    }
  };

  return (
    <div className="container mt-5">
      <h1 className="mb-4">TNGL Customer Management</h1>

      {/* Customer Form */}
      <form onSubmit={handleSubmit}>
        <div className="form-row">
          {/* Your form inputs here */}
        </div>
        <button type="submit" className="btn btn-primary">
          Add Customer
        </button>
      </form>

      {/* Customer List */}
      <h2 className="mt-4">Customer List</h2>
      <ul className="list-group">
        {customers.map((customer) => (
          <li key={customer._id} className="list-group-item d-flex justify-content-between align-items-center">
            {customer.name} - {customer.customerNumber}
            <div className="btn-group">
              <button
                className="btn btn-warning"
                onClick={() => handleUpdateClick(customer._id)}
              >
                Update
              </button>
              <button className="btn btn-danger" onClick={() => handleDelete(customer._id)}>
                Delete
              </button>
            </div>
            {updateCustomerId === customer._id && (
              <div className="modal" tabIndex="-1" role="dialog" style={{ display: 'block', backgroundColor: 'rgba(0,0,0,0.5)' }}>
                <div className="modal-dialog" role="document">
                  <div className="modal-content">
                    <div className="modal-header">
                      <h5 className="modal-title">Update Customer</h5>
                      <button type="button" className="close" onClick={handleCancelUpdate}>
                        <span>&times;</span>
                      </button>
                    </div>
                    <div className="modal-body">
                      {/* Form fields for updating customer */}
                    </div>
                    <div className="modal-footer">
                      <button type="button" className="btn btn-primary" onClick={handleUpdateSubmit}>
                        Update Customer
                      </button>
                      <button type="button" className="btn btn-secondary" onClick={handleCancelUpdate}>
                        Cancel
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Dashboard;
