// src/components/Dashboard.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const API_URL = 'http://localhost:5000/api/customers';

const Dashboard = () => {
  const [customers, setCustomers] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    address: '',
    customerNumber: '',
    meterSerialNumber: '',
  });

  const [updateCustomerId, setUpdateCustomerId] = useState(null);

  useEffect(() => {
    fetchCustomers();
  }, []);

  const fetchCustomers = async () => {
    try {
      const response = await axios.get(API_URL);
      setCustomers(response.data);
    } catch (error) {
      console.error('Error fetching customers:', error.message);
    }
  };

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(API_URL, formData);
      setFormData({ name: '', address: '', customerNumber: '', meterSerialNumber: '' });
      fetchCustomers();
    } catch (error) {
      console.error('Error creating customer:', error.message);
    }
  };

  const handleUpdateClick = (id) => {
    setUpdateCustomerId(id);
  };

  const handleUpdateSubmit = async () => {
    try {
      const updatedFields = { ...formData };
      await axios.put(`${API_URL}/${updateCustomerId}`, updatedFields);
      setUpdateCustomerId(null);
      setFormData({ name: '', address: '', customerNumber: '', meterSerialNumber: '' });
      fetchCustomers();
    } catch (error) {
      console.error('Error updating customer:', error.message);
    }
  };

  const handleCancelUpdate = () => {
    setUpdateCustomerId(null);
    setFormData({ name: '', address: '', customerNumber: '', meterSerialNumber: '' });
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`${API_URL}/${id}`);
      fetchCustomers();
    } catch (error) {
      console.error('Error deleting customer:', error.message);
    }
  };

  return (
    <div className="container mt-5">
      <h1 className="mb-4">TNGL Customer Management</h1>

      {/* Customer Form */}
      <form onSubmit={handleSubmit}>
        <div className="form-row">
          <div className="col-md-3 mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Name"
              name="name"
              value={formData.name}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="col-md-3 mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Address"
              name="address"
              value={formData.address}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="col-md-3 mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Customer Number"
              name="customerNumber"
              value={formData.customerNumber}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="col-md-3 mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Meter Serial Number"
              name="meterSerialNumber"
              value={formData.meterSerialNumber}
              onChange={handleInputChange}
              required
            />
          </div>
        </div>
        <button type="submit" className="btn btn-primary">
          {updateCustomerId ? 'Update Customer' : 'Add Customer'}
        </button>
      </form>

      {/* Customer List */}
      <h2 className="mt-4">Customer List</h2>
      <ul className="list-group">
        {customers.map((customer) => (
          <li key={customer._id} className="list-group-item d-flex justify-content-between align-items-center">
            {customer.name} - {customer.customerNumber}
            <div className="btn-group">
              <button
                className="btn btn-warning"
                data-toggle="modal"
                data-target="#updateCustomerModal"
                onClick={() => handleUpdateClick(customer._id)}
              >
                Update
              </button>
              <button className="btn btn-danger" onClick={() => handleDelete(customer._id)}>
                Delete
              </button>
            </div>
          </li>
        ))}
      </ul>

      {/* Update Customer Modal */}
      <div className="modal" id="updateCustomerModal">
        <div className="modal-dialog modal-lg">
          <div className="modal-content">
            <div className="modal-header">
              <h4 className="modal-title">Update Customer</h4>
              <button type="button" className="close" data-dismiss="modal" onClick={handleCancelUpdate}>&times;</button>
            </div>
            <div className="modal-body">
              {/* Form fields for updating customer */}
              <div className="form-row">
                <div className="col-md-3 mb-3">
                  <input
                    type="text"
                    className="form-control"
                    placeholder="Name"
                    name="name"
                    value={formData.name}
                    onChange={handleInputChange}
                    required
                  />
                </div>
                <div className="col-md-3 mb-3">
                  <input
                    type="text"
                    className="form-control"
                    placeholder="Address"
                    name="address"
                    value={formData.address}
                    onChange={handleInputChange}
                    required
                  />
                </div>
                <div className="col-md-3 mb-3">
                  <input
                    type="text"
                    className="form-control"
                    placeholder="Customer Number"
                    name="customerNumber"
                    value={formData.customerNumber}
                    onChange={handleInputChange}
                    required
                  />
                </div>
                <div className="col-md-3 mb-3">
                  <input
                    type="text"
                    className="form-control"
                    placeholder="Meter Serial Number"
                    name="meterSerialNumber"
                    value={formData.meterSerialNumber}
                    onChange={handleInputChange}
                    required
                  />
                </div>
              </div>
              <div className="btn-group mt-2">
                <button type="button" className="btn btn-primary" onClick={handleUpdateSubmit}>
                  Update Customer
                </button>
                <button type="button" className="btn btn-secondary" onClick={handleCancelUpdate}>
                  Cancel
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
