import React, { useState, useEffect } from 'react';
import axios from 'axios';

const API_URL = 'http://localhost:5000/api/customers';

const Dashboard = () => {
  const [customers, setCustomers] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    address: '',
    customerNumber: '',
    meterSerialNumber: '',
  });

  const [updateCustomerId, setUpdateCustomerId] = useState(null);

  useEffect(() => {
    fetchCustomers();
  }, []);

  const fetchCustomers = async () => {
    try {
      const response = await axios.get(API_URL);
      setCustomers(response.data);
    } catch (error) {
      console.error('Error fetching customers:', error.message);
    }
  };

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(API_URL, formData);
      setFormData({ name: '', address: '', customerNumber: '', meterSerialNumber: '' });
      fetchCustomers();
    } catch (error) {
      console.error('Error creating customer:', error.message);
    }
  };

  const handleUpdateClick = (id) => {
    setUpdateCustomerId(id);
  };

  const handleUpdateSubmit = async () => {
    try {
      const existingCustomer = customers.find((customer) => customer._id === updateCustomerId);

      const updatedFields = {};
      Object.keys(formData).forEach((key) => {
        if (formData[key] !== existingCustomer[key]) {
          updatedFields[key] = formData[key];
        }
      });

      await axios.put(`${API_URL}/${updateCustomerId}`, updatedFields);
      setUpdateCustomerId(null);
      fetchCustomers();
    } catch (error) {
      console.error('Error updating customer:', error.message);
    }
  };

  const handleCancelUpdate = () => {
    setFormData({ name: '', address: '', customerNumber: '', meterSerialNumber: '' });
    setUpdateCustomerId(null);
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`${API_URL}/${id}`);
      fetchCustomers();
    } catch (error) {
      console.error('Error deleting customer:', error.message);
    }
  };

  return (
    <div className="container-xl">
      <div className="table-responsive">
        <div className="table-wrapper">
          <div className="table-title">
            <div className="row">
              <div className="col-sm-6">
                <h2>TNGL Customer <b>Dashboard</b></h2>
              </div>
              <div className="col-sm-6">
                <a href="#addCustomerModal" className="btn btn-success" data-toggle="modal">
                  <i className="material-icons">&#xE147;</i>
                  <span>Add New Customer</span>
                </a>
              </div>
            </div>
          </div>

          {/* Customer Form */}
          <form onSubmit={handleSubmit}>
            <div className="form-row">
              <div className="col-md-3 mb-3">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Name"
                  name="name"
                  value={formData.name}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="col-md-3 mb-3">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Address"
                  name="address"
                  value={formData.address}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="col-md-3 mb-3">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Customer Number"
                  name="customerNumber"
                  value={formData.customerNumber}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="col-md-3 mb-3">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Meter Serial Number"
                  name="meterSerialNumber"
                  value={formData.meterSerialNumber}
                  onChange={handleInputChange}
                  required
                />
              </div>
            </div>
            <button type="submit" className="btn btn-primary">
              Add Customer
            </button>
          </form>

          {/* Customer List */}
          <h2 className="mt-4">Customer List</h2>
          <table className="table table-striped table-hover">
            <thead>
              <tr>
                <th>Name</th>
                <th>Address</th>
                <th>Customer Number</th>
                <th>Meter Serial Number</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {customers.map((customer) => (
                <tr key={customer._id}>
                  <td>{customer.name}</td>
                  <td>{customer.address}</td>
                  <td>{customer.customerNumber}</td>
                  <td>{customer.meterSerialNumber}</td>
                  <td>
                    <button
                      className="btn btn-warning"
                      onClick={() => handleUpdateClick(customer._id)}
                    >
                      Update
                    </button>
                    <button className="btn btn-danger" onClick={() => handleDelete(customer._id)}>
                      Delete
                    </button>
                  </td>
                  {updateCustomerId === customer._id && (
                    <td>
                      <div className="form-row">
                        <div className="col-md-3 mb-3">
                          <input
                            type="text"
                            className="form-control"
                            placeholder="Name"
                            name="name"
                            value={formData.name}
                            onChange={handleInputChange}
                            required
                          />
                        </div>
                        <div className="col-md-3 mb-3">
                          <input
                            type="text"
                            className="form-control"
                            placeholder="Address"
                            name="address"
                            value={formData.address}
                            onChange={handleInputChange}
                            required
                          />
                        </div>
                        <div className="col-md-3 mb-3">
                          <input
                            type="text"
                            className="form-control"
                            placeholder="Customer Number"
                            name="customerNumber"
                            value={formData.customerNumber}
                            onChange={handleInputChange}
                            required
                          />
                        </div>
                        <div className="col-md-3 mb-3">
                          <input
                            type="text"
                            className="form-control"
                            placeholder="Meter Serial Number"
                            name="meterSerialNumber"
                            value={formData.meterSerialNumber}
                            onChange={handleInputChange}
                            required
                          />
                        </div>
                      </div>
                      <div className="btn-group mt-2">
                        <button
                          type="button"
                          className="btn btn-primary"
                          onClick={handleUpdateSubmit}
                        >
                          Update Customer
                        </button>
                        <button
                          type="button"
                          className="btn btn-secondary"
                          onClick={handleCancelUpdate}
                        >
                          Cancel
                        </button>
                      </div>
                    </td>
                  )}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
