// src/components/Dashboard.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const API_URL = 'http://localhost:5000/api/customers';

const Dashboard = () => {
  const [customers, setCustomers] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    address: '',
    customerNumber: '',
    meterSerialNumber: '',
  });

  // State to store the ID of the customer being updated
  const [updateCustomerId, setUpdateCustomerId] = useState(null);

  useEffect(() => {
    fetchCustomers();
  }, []);

  const fetchCustomers = async () => {
    try {
      const response = await axios.get(API_URL);
      setCustomers(response.data);
    } catch (error) {
      console.error('Error fetching customers:', error.message);
    }
  };

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(API_URL, formData);
      setFormData({ name: '', address: '', customerNumber: '', meterSerialNumber: '' });
      fetchCustomers();
    } catch (error) {
      console.error('Error creating customer:', error.message);
    }
  };

  const handleUpdateClick = (id) => {
    // Set the ID of the customer to be updated
    setUpdateCustomerId(id);
  };

  const handleUpdateSubmit = async (field) => {
    try {
      // Get the existing customer data
      const existingCustomer = customers.find((customer) => customer._id === updateCustomerId);

      // Create an update object with only the changed field
      const updatedFields = {
        [field]: formData[field],
      };

      // Update the customer with the provided ID and the changed field
      await axios.put(`${API_URL}/${updateCustomerId}`, updatedFields);
      fetchCustomers();
    } catch (error) {
      console.error(`Error updating customer ${field}:`, error.message);
    }
  };

  const handleUpdateAll = async () => {
    try {
      // Update all fields for the customer with the provided ID
      await axios.put(`${API_URL}/${updateCustomerId}`, formData);
      setUpdateCustomerId(null);
      fetchCustomers();
    } catch (error) {
      console.error('Error updating customer:', error.message);
    }
  };

  const handleCancelUpdate = () => {
    // Reset the form data
    setFormData({ name: '', address: '', customerNumber: '', meterSerialNumber: '' });
    setUpdateCustomerId(null);
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`${API_URL}/${id}`);
      fetchCustomers();
    } catch (error) {
      console.error('Error deleting customer:', error.message);
    }
  };

  return (
    <div className="container mt-5">
      <h1 className="mb-4">TNGL Customer Management</h1>

      {/* Customer Form */}
      <form onSubmit={handleSubmit}>
        <div className="form-row">
          <div className="col-md-3 mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Name"
              name="name"
              value={formData.name}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="col-md-3 mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Address"
              name="address"
              value={formData.address}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="col-md-3 mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Customer Number"
              name="customerNumber"
              value={formData.customerNumber}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="col-md-3 mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Meter Serial Number"
              name="meterSerialNumber"
              value={formData.meterSerialNumber}
              onChange={handleInputChange}
              required
            />
          </div>
        </div>
        <button type="submit" className="btn btn-primary">
          Add Customer
        </button>
      </form>

      {/* Customer List */}
      <h2 className="mt-4">Customer List</h2>
      <ul className="list-group">
        {customers.map((customer) => (
          <li key={customer._id} className="list-group-item d-flex justify-content-between align-items-center">
            <div>
              <strong>Name:</strong> {customer.name} <br />
              <strong>Address:</strong> {customer.address} <br />
              <strong>Customer Number:</strong> {customer.customerNumber} <br />
              <strong>Meter Serial Number:</strong> {customer.meterSerialNumber} <br />
            </div>
            <div className="btn-group">
              {/* Update buttons for individual fields */}
              <button
                className="btn btn-warning"
                onClick={() => handleUpdateSubmit('name')}
              >
                Update Name
              </button>
              <button
                className="btn btn-warning"
                onClick={() => handleUpdateSubmit('address')}
              >
                Update Address
              </button>
              <button
                className="btn btn-warning"
                onClick={() => handleUpdateSubmit('customerNumber')}
              >
                Update Customer Number
              </button>
              <button
                className="btn btn-warning"
                onClick={() => handleUpdateSubmit('meterSerialNumber')}
              >
                Update Meter Serial Number
              </button>

              {/* Update All button */}
              <button
                className="btn btn-primary"
                onClick={() => handleUpdateAll()}
              >
                Update All
              </button>
              <button className="btn btn-danger" onClick={() => handleDelete(customer._id)}>
                Delete
              </button>
            </div>
            {updateCustomerId === customer._id && (
              <div className="mt-2">
                {/* Form fields for updating customer */}
                <div className="form-row">
                  <div className="col-md-3 mb-3">
                    <input
                      type="text"
                      className="form-control"
                      placeholder="Name"
                      name="name"
                      value={formData.name}
                      onChange={handleInputChange}
                      required
                    />
                  </div>
                  <div className="col-md-3 mb-3">
                    <input
                      type="text"
                      className="form-control"
                      placeholder="Address"
                      name="address"
                      value={formData.address}
                      onChange={handleInputChange}
                      required
                    />
                  </div>
                  <div className="col-md-3 mb-3">
                    <input
                      type="text"
                      className="form-control"
                      placeholder="Customer Number"
                      name="customerNumber"
                      value={formData.customerNumber}
                      onChange={handleInputChange}
                      required
                    />
                  </div>
                  <div className="col-md-3 mb-3">
                    <input
                      type="text"
                      className="form-control"
                      placeholder="Meter Serial Number"
                      name="meterSerialNumber"
                      value={formData.meterSerialNumber}
                      onChange={handleInputChange}
                      required
                    />
                  </div>
                </div>
                <div className="btn-group mt-2">
                  <button type="button" className="btn btn-primary" onClick={() => handleUpdateSubmit()}>
                    Update Customer
                  </button>
                  <button type="button" className="btn btn-secondary" onClick={handleCancelUpdate}>
                    Cancel
                  </button>
                </div>
              </div>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Dashboard;
