import React, { useState, useEffect } from 'react';
import axios from 'axios';

const API_URL = 'http://localhost:5000/api/customers';

const Dashboard = () => {
  const [customers, setCustomers] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    address: '',
    customerNumber: '',
    meterSerialNumber: '',
  });
  const [updateCustomerId, setUpdateCustomerId] = useState(null);

  useEffect(() => {
    fetchCustomers();
  }, []);

  const fetchCustomers = async () => {
    try {
      const response = await axios.get(API_URL);
      setCustomers(response.data);
    } catch (error) {
      console.error('Error fetching customers:', error.message);
    }
  };

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(API_URL, formData);
      setFormData({ name: '', address: '', customerNumber: '', meterSerialNumber: '' });
      fetchCustomers();
    } catch (error) {
      console.error('Error creating customer:', error.message);
    }
  };

  const handleUpdateClick = (id) => {
    const customerToUpdate = customers.find((customer) => customer._id === id);
    setFormData({ ...customerToUpdate });
    setUpdateCustomerId(id);
    // Open the modal using JavaScript
    document.getElementById('updateCustomerModal').style.display = 'block';
  };

  const handleUpdateSubmit = async () => {
    try {
      await axios.put(`${API_URL}/${updateCustomerId}`, formData);
      setUpdateCustomerId(null);
      setFormData({ name: '', address: '', customerNumber: '', meterSerialNumber: '' });
      fetchCustomers();
      // Close the modal using JavaScript
      document.getElementById('updateCustomerModal').style.display = 'none';
    } catch (error) {
      console.error('Error updating customer:', error.message);
    }
  };

  const handleCancelUpdate = () => {
    setUpdateCustomerId(null);
    setFormData({ name: '', address: '', customerNumber: '', meterSerialNumber: '' });
    // Close the modal using JavaScript
    document.getElementById('updateCustomerModal').style.display = 'none';
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`${API_URL}/${id}`);
      fetchCustomers();
    } catch (error) {
      console.error('Error deleting customer:', error.message);
    }
  };

  return (
    <div className="container-xl mt-4">
      <div className="table-responsive">
        <table className="table table-bordered table-hover">
          <thead>
            <tr>
              <th>Name</th>
              <th>Address</th>
              <th>Customer Number</th>
              <th>Meter Serial Number</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {customers.map((customer) => (
              <tr key={customer._id}>
                <td>{customer.name}</td>
                <td>{customer.address}</td>
                <td>{customer.customerNumber}</td>
                <td>{customer.meterSerialNumber}</td>
                <td>
                  <button
                    className="btn btn-warning mr-2"
                    onClick={() => handleUpdateClick(customer._id)}
                  >
                    Update
                  </button>
                  <button className="btn btn-danger" onClick={() => handleDelete(customer._id)}>
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Add Customer Form */}
      <div className="card mt-4">
        <div className="card-header">
          <h3>Add New Customer</h3>
        </div>
        <div className="card-body">
          <form onSubmit={handleSubmit}>
            <div className="form-row">
              <div className="col-md-3 mb-3">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Name"
                  name="name"
                  value={formData.name}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="col-md-3 mb-3">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Address"
                  name="address"
                  value={formData.address}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="col-md-3 mb-3">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Customer Number"
                  name="customerNumber"
                  value={formData.customerNumber}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="col-md-3 mb-3">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Meter Serial Number"
                  name="meterSerialNumber"
                  value={formData.meterSerialNumber}
                  onChange={handleInputChange}
                  required
                />
              </div>
            </div>
            <button type="submit" className="btn btn-primary">
              Add Customer
            </button>
          </form>
        </div>
      </div>

      {/* Update Customer Modal */}
      <div
        className="modal fade"
        id="updateCustomerModal"
        tabIndex="-1"
        role="dialog"
        style={{ display: 'none' }}
      >
        <div className="modal-dialog modal-lg" role="document">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title">Update Customer</h5>
              <button
                type="button"
                className="close"
                data-dismiss="modal"
                aria-label="Close"
                onClick={handleCancelUpdate}
              >
                <span aria-hidden="true">&times;</span>
              </button>
            </div>
            <div className="modal-body">
              <form>
                <div className="form-row">
                  <div className="col-md-3 mb-3">
                    <label htmlFor="updateName">Name</label>
                    <input
                      type="text"
                      className="form-control"
                      id="updateName"
                      placeholder="Name"
                      name="name"
                      value={formData.name}
                      onChange={handleInputChange}
                    />
                  </div>
                  <div className="col-md-3 mb-3">
                    <label htmlFor="updateAddress">Address</label>
                    <input
                      type="text"
                      className="form-control"
                      id="updateAddress"
                      placeholder="Address"
                      name="address"
                      value={formData.address}
                      onChange={handleInputChange}
                    />
                  </div>
                  <div className="col-md-3 mb-3">
                    <label htmlFor="updateCustomerNumber">Customer Number</label>
                    <input
                      type="text"
                      className="form-control"
                      id="updateCustomerNumber"
                      placeholder="Customer Number"
                      name="customerNumber"
                      value={formData.customerNumber}
                      onChange={handleInputChange}
                    />
                  </div>
                  <div className="col-md-3 mb-3">
                    <label htmlFor="updateMeterSerialNumber">Meter Serial Number</label>
                    <input
                      type="text"
                      className="form-control"
                      id="updateMeterSerialNumber"
                      placeholder="Meter Serial Number"
                      name="meterSerialNumber"
                      value={formData.meterSerialNumber}
                      onChange={handleInputChange}
                    />
                  </div>
                </div>
              </form>
            </div>
            <div className="modal-footer">
              <button
                type="button"
                className="btn btn-primary"
                onClick={handleUpdateSubmit}
              >
                Update Customer
              </button>
              <button
                type="button"
                className="btn btn-secondary"
                data-dismiss="modal"
                onClick={handleCancelUpdate}
              >
                Cancel
              </button>
